    const handleSetFontSize = () => {
        setFontSize(tempFontSize);
    };

    return (
        <div id="settings">
            <h1>Ë®≠ÂÆö„Éö„Éº„Ç∏</h1>

            {/* ÊñáÂ≠ó„Çµ„Ç§„Ç∫„Çª„ÇØ„Ç∑„Éß„É≥ */}
